import React, { useRef } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate(); // Hook called inside the component
  const usernameRef = useRef(null);
  const passwordRef = useRef(null);
  const paraRef = useRef(null);

  const myfun = () => {
    const username = usernameRef.current.value;
    const password = passwordRef.current.value;
    if (username === "admin" && password === "admin") {
      navigate("/"); // Navigate to Home component
    } else {
      if (paraRef.current) {
        paraRef.current.innerHTML = "Invalid Credentials";
      }
    }
  };

  const skip = () => {
    navigate("/"); // Navigate to Home component
  };


  return (
    <div>
        
    <div class="login">
        <div class="loginbox">
            <p onclick={skip()}>Skip</p>
            <h2>LOGIN</h2><br/>
            <label for="username"> USER NAME</label>
            <input type="text" id="username"/><br/><br/>
            <label for="password">PASSWORD</label>
            <input type="password" name="password" id="password"/><br/><br/>
            <center><button onclick={myfun()}>login</button></center>
        </div>
    </div>
    </div>
  );
};

export default Login;
