import React, { useRef } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate(); // Hook called inside the component
  const usernameRef = useRef(null);
  const passwordRef = useRef(null);
  const paraRef = useRef(null);

  const myfun = () => {
    const username = usernameRef.current.value;
    const password = passwordRef.current.value;
    if (username === "admin" && password === "admin") {
      navigate("/home"); // Navigate to Home component
    } else {
      if (paraRef.current) {
        paraRef.current.innerHTML = "Invalid Credentials";
      }
    }
  };

  const skip = () => {
    navigate("/home"); // Navigate to Home component
  };


  return (
    <div>
        <div class="nav">
        <div class="logo">
            <img src="https://cdn.pixabay.com/photo/2013/07/12/15/53/shopping-cart-150504_1280.png" alt="no image" width="100px" style={{padding: "10px;"}} />
        </div>
        <div class="memu">
            <a href="/">Home</a>
            <a href="/products">Products</a>
            <a href="/contact">Contact us</a>
        </div>
        <div class="btns">
            <button><a href="./login.html">login</a></button>
        </div>
    </div>
    <div class="login">
        <div class="loginbox">
            <p onclick="skip()">Skip</p>
            <h2>LOGIN</h2><br/>
            <label for="username"> USER NAME</label>
            <input type="text" id="username"/><br/><br/>
            <label for="password">PASSWORD</label>
            <input type="password" name="password" id="password"/><br/><br/>
            <center><button onclick="myfun()">login</button></center>
        </div>
    </div>
    </div>
  );
};

export default Login;
