document.addEventListener("DOMContentLoaded", () => {
    async function fetchdata() {
        try {
            let response = await fetch("https://fakestoreapi.com/products");
            let data = await response.json();
            let referElement = document.getElementById("container"); // Ensure the id matches
            if (!referElement) {
                console.error("Element with id 'container' not found");
                return;
            }
            data.forEach(product => {
                referElement.innerHTML += `
                    <div className="card">
                        <img src=${product.image} alt=""/>
                        <h2>${product.title.slice(0, 50)}</h2>
                        <p>${product.description.slice(0, 50)}</p>
                        <div className="btns">
                            <button className="price">$${product.price}/-</button>
                            <button className="rating"><i className="fa-solid fa-star">${product.rating.rate}</i></button>
                        </div>
                    </div>
                `;
            });
        } catch (error) {
            console.error("Error fetching product data:", error);
        }
    }

    async function fetchdata1() {
        try {
            let response = await fetch("https://fakestoreapi.com/products");
            let data = await response.json();
            let content = '';
            let mainElement = document.getElementById("main"); // Ensure the id matches
            if (!mainElement) {
                console.error("Element with id 'main' not found");
                return;
            }
            data.slice(0, 3).forEach(product => {
                content += `
                    <div className="box">
                        <img src="${product.image}" alt="">
                        <h2>${product.title.slice(0, 10)}</h2>
                        <p>${product.description.slice(0, 20)}</p>
                        <div className="btns">
                            <button className="price">$${product.price}/-</button>
                            <button className="rating"><i className="fa-solid fa-star">${product.rating.rate}</i></button>
                        </div>
                    </div>
                `;
            });
            mainElement.innerHTML = content;
        } catch (error) {
            console.error("Error fetching product data:", error);
            let containerElement = document.getElementById("container"); // Fallback if 'container' is used
            if (containerElement) {
                containerElement.innerHTML = "<p>Failed to load products. Please try again later.</p>";
            }
        }
    }

    function myfun() {
        let username = document.getElementById('username').value;
        let password = document.getElementById('password').value;
        if (username === "admin" && password === "admin") {
            window.location = "index.html";
        } else {
            let paraElement = document.getElementById('para');
            if (paraElement) {
                paraElement.innerHTML = "Invalid Credentials";
            }
        }
    }

    function skip() {
        window.location = "index.html";
    }

    // Fetch product data
    fetchdata();
    fetchdata1();
});
